// import { Button } from '@material-ui/core'
// import Button from '@mui/material/Button'
import React, { useEffect, useRef } from 'react';

const loadScript = (src) => new Promise((resolve, reject) => {
  if (document.querySelector(`script[src="${src}"]`)) return resolve();
  const script = document.createElement('script');
  script.src = src;
  script.onload = () => resolve();
  script.onerror = (err) => reject(err);
  document.body.appendChild(script);
});

const GoogleAuth = () => {
// const { token, setToken, setGoogleId } = useContext(AuthContext);
  const googleButton = useRef(null);

  useEffect(() => {
    console.log('EFFECT RUNNING');
    const src = 'https://accounts.google.com/gsi/client';
    const id = '212943273540-hbq3r9sufv65m1qk6g4h0iod1msgb7sj.apps.googleusercontent.com';

    function handleCredentialResponse(response) {
      console.log('Encoded JWT ID token: ' + response.credential);
      console.log('Decoded JWT ID token: ' + JSON.stringify(parseJwt(response.credential)));
    }

    loadScript(src)
      .then(() => {
        /* global google */
        console.log(google);
        google.accounts.id.initialize({
          client_id: id,
          callback: handleCredentialResponse,
        });
        google.accounts.id.renderButton(
          googleButton.current,
          { theme: 'contained', size: 'medium' },
        );
      })
      .catch(console.error);

    return () => {
      const scriptTag = document.querySelector(`script[src="${src}"]`);
      if (scriptTag) document.body.removeChild(scriptTag);
    };
  }, []);

  function parseJwt(token) {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(atob(base64).split('').map(function (c) {
      return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));

    return JSON.parse(jsonPayload);
  }

  return (
    <button type="button" fullWidth variant="contained" ref={googleButton}>Sign In</button>
  );
};

export default GoogleAuth;
