import { useState, useEffect } from 'react'

const MyMessage = ({ message }) => {
  const [messageState, setMessageState] = useState(message.text)

  useEffect(() => {
    console.log('text', message.text)
  }, [messageState])

  const onDeleteMessage = (id) => {
    setMessageState('message has been deleted')
    document.getElementById(id).style.backgroundColor = 'grey';
    if (document.getElementById) {
      document.getElementById(id).className = 'deleted'
    }
  }

  if (message.attachments && message.attachments.length > 0) {
    return (
      <div>
        <img
          src={message.attachments[0].file}
          alt="message-attachment"
          className="message-image"
          style={{ float: 'right', height: '150px' }}
        ></img>

      </div>
    );
  }

  return (
    <div id={message.id} className="message" style={{ float: 'right', marginRight: '18px', color: 'white', backgroundColor: '#3B2A50' }}>
      <button style={{ borderRadius: '5px', height: '20px', backgroundColor: 'red' }} onClick={() => onDeleteMessage(message.id)}><img src='https://e7.pngegg.com/pngimages/36/528/png-clipart-computer-icons-button-wiki-delete-button-angle-rectangle-thumbnail.png' style={{height:'10px', width:'5px'}} /></button>
      {messageState}
    </div>
  );
};

export default MyMessage;
