import { useState, useEffect } from 'react'

const MyMessage = ({ message }, props) => {
  const [messageState, setMessageState] = useState(message.text)
  useEffect(()=> {
    console.log('text', message.text)
  }, [messageState])

  if (message.attachments && message.attachments.length > 0) {
    return (
      <img
        src={message.attachments[0].file}
        alt="message-attachment"
        className="message-image"
        style={{ float: 'right', height: '150px' }}
      />
    );
  }

  const onDeleteMessage = () => {
    setMessageState('message has been deleted')
    // document.getElementsByClassName("ce-message-bubble").style.color = "blue"
    document.getElementsByClassName('ce-message-bubble')[0].style.backgroundColor = 'blue';
  }

  return (
    <div id={props.id} className="message" style={{ float: 'right', marginRight: '18px', color: 'white', backgroundColor: '#3B2A50' }}>
      <button style={{ borderRadius: '5px', height: '5px', backgroundColor: 'grey' }} onClick={onDeleteMessage}> </button>  
      {messageState}
    </div>
  );
};

export default MyMessage;
